import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Drawer from '@material-ui/core/Drawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import MoreIcon from '@material-ui/icons/MoreVert';
import "../CSS/SideBar.css"


const useStyles = makeStyles(theme => ({
  root: {
      flexGrow: 1,
  },
  list: {
      maxHeight: 240,
  },
  menuButton: {
      marginRight: theme.spacing(2),
      marginTop: theme.spacing(2)
  },
  sideList: {
    width: 'auto',
  },
  rightList: {
    marginRight: theme.spacing(2),
    marginTop: theme.spacing(2),
    marginLeft: theme.spacing(2),
    position: 'absolute',
    right: 0,
    top: 0
  }
}));

function defaultFunction(){
  return;
}

export default function SideBar() {
  const classes = useStyles();
  const [state, setState] = React.useState({
    left: false,
    right: false,
  });
  const currentList = {'left':{'Show/Hide Music': defaultFunction(),
                              'Show/Hide Pages': defaultFunction(), 
                              'Change Dance Name': defaultFunction(), 
                              'Change Dancer Number':defaultFunction() 
                            }, 
                       'right':{'Default Formation': defaultFunction(), 
                                'Copy previous formation': defaultFunction(), 
                                'Change dots information': defaultFunction(),
                                'Settings':defaultFunction()}
                              };

  const toggleDrawer = (side, open) => event => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }

    setState({ ...state, [side]: open });
  };

  const sideList = side => (
    <div
      className={classes.list}
      onClick={toggleDrawer(side, false)}
      onKeyDown={toggleDrawer(side, false)}
      style={{maxHeight: 360}}
    >
      <List component="nav" aria-label="main mailbox folders">
        {currentList[side].array.forEach(([key, value]) => {
          <ListItem button key={key} onClick={value}>
            <ListItemText primary={key}/>
          </ListItem>
        })
        }
        {/* {currentList[side].products.map(([key, value], index) => (
          <ListItem button key={key} onClick={value}>
            <ListItemText primary={key}/>
          </ListItem>
        ))} */}
      </List>
    </div>
  );

  return (
  <div className={classes.root}>
      <Toolbar>
        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="Menu" onClick={toggleDrawer('left', true)}>
          <MenuIcon />
        </IconButton>
        <Drawer type="temporary" classes={{paper: classes.list}} open={state.left} onClose={toggleDrawer('left', false)}>
          {sideList('left')}
        </Drawer>

        <IconButton className={classes.rightList} color="inherent" anchor="right" onClick={toggleDrawer('right', true)}>
          <MoreIcon />
        </IconButton>
        <Drawer type="temporary" classes={{paper: classes.list}} className={classes.rightList} anchor="right" open={state.right} onClose={toggleDrawer('right', false)} style={{ flex: 1 }}>
          {sideList('right')}
        </Drawer>
      </Toolbar>
  </div>
  );
}
