import React, {Component} from 'react';
import SideBar from './Components/SideBar';
import FormationCanvas from './Components/FormationCanvas';
import {Card, Grid, Paper} from '@material-ui/core';
import {makeStyles} from "@material-ui/core";
import '../css/MainPage.css';
import Header from "./Components/Header";
import MediaControl from "./Components/Music";
import Typography from '@material-ui/core/Typography';
import Player from "./Components/MusicPlayer/Player";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
import TextField from '@material-ui/core/TextField';
import Button from "@material-ui/core/Button";

const useStyles = makeStyles(theme => ({
    items: {
        marginTop: theme.spacing(4),
    },
    Paper: {
        width: '100%',
         padding: 20,
         marginTop:10,
         marginBottom: 10,
     },
}));

const Transition = React.forwardRef(function Transition(props, ref){
    return <Slide direction="up" ref={ref} {...props}/>;
  });  

const currentlyAtWelcome = window.location.pathname == "/";

const [showInfo, setShowInfo] = React.useState(false);

function showInfoPopover(){
    setShowInfo(true);
}

function closeInfoPopover(){
    setShowInfo(false);
}

function onDanceNameChange(e){
    this.setState({danceName: e.target.value});
}

function onDancerNumberChange(e){
    this.setState({dancerNumber: e.target.value});
}

class MainPage extends Component{

    constructor(props){
        super(props);
        this.state = {
            danceName: "Main",
        }
    }

    componentDidMount (){
        this.setState({
            danceName: this.props.location.state.danceName,
        })
    }

    render(){
        return (
            <React.Fragment>
                <Header headerTitle={this.state.danceName}/>
                <SideBar showInfo={showInfo}/>
                <Dialog open={showInfo} TransitionComponent={Transition} keepMounted onClose={closeInfoPopover}
                aria-labelledby="alert-dialog-slide-title" aria-describedby="alert-dialog-slide-description" >
                    <DialogTitle id="alert-dialog-slide-title">{"Change Basic Dance Info"}</DialogTitle>
                     <DialogContent>
                         <DialogContentText id="dance-name-dialog">
                            Dance Name:
                        </DialogContentText>
                        <TextField autoFocus margin="dense" id="name" label="Dance Name" type="dance name" value={this.state.danceName} 
                                onChange={onDanceNameChange} fullWidth/>
                        <DialogContentText id="dancer-number-dialog">
                            Dancer Number:
                        </DialogContentText>
                        <TextField autoFocus margin="dense" id="number" label="Dancer Number" type="dancer number" value={this.state.dancerNumber}
                                   onChange={onDancerNumberChange} fullWidth/>
                        </DialogContent>

                        <DialogActions>
                            <Button onClick={changeInfo}>Confirm</Button>
                            <Button onClick={handleInfoClose}>Cancel</Button>
                        </DialogActions>
                </Dialog>
                <Grid className={useStyles.items} container direction="column"  >
                    <Grid item>
                        <FormationCanvas dancerNumber={this.state.dancerNumber}/>
                    </Grid>
                </Grid>
            </React.Fragment>
        );
    }
}

export default MainPage;
