  describe('Convert', function () {
    for (var from in tests) {
      var test = tests[from];
        for (var to in test.convert) {
          itShouldConvert(from, to, test.convert[to]);
        }
    }

    function itShouldConvert (from, to, tests) {
      it('should convert '+from+' to '+to, function () {
        tests.forEach(function (test) {
          var input = test[0];
          var expected = test[1];
          var actual;

          var fromSimple = (from === 'fromHex' || from === 'fromGrayscale');
          var toSimple = (to === 'toHex' || to === 'toGrayscale');

          var fromArray = from + 'Array';
          var fromObject = from + 'Object';
          var toArray = to + 'Array';
          var toObject = to + 'Object';
          var toRawArray = toArray.replace('to', 'toRaw');
          var toRawObject = toObject.replace('to', 'toRaw');

          if (fromSimple && toSimple) {
            // fromHex().toGrayscale()
            actual = Colr[from](input)[to]();
            compare(actual, expected);
          }

          if (fromSimple && ! toSimple) {
            // fromHex().toRgbArray()
            actual = Colr[from](input)[toArray]();
            compare(actual, round(expected));

            // fromHex().toRgbObject()
            actual = Colr[from](input)[toObject]();
            compare(actual, arr2obj(to, round(expected)));

            // fromHex().toRawRgbArray()
            actual = Colr[from](input)[toRawArray]();
            compare(round2(actual), expected);

            // fromHex().toRawRgbObject()
            actual = Colr[from](input)[toRawObject]();
            compare(round2(actual), arr2obj(to, expected));
          }

          if (! fromSimple && toSimple) {
            // fromRgb().toHex()
            actual = Colr[from].apply(null, input)[to]();
            compare(round2(actual), expected);

            // fromRgbArray().toHex()
            actual = Colr[fromArray](input)[to]();
            compare(round2(actual), expected);

            // fromRgbObject().toHex()
            actual = Colr[fromObject](arr2obj(from, input))[to]();
            compare(round2(actual), expected);
          }

          if (! fromSimple && ! toSimple) {
            // fromRgb().toRgbArray()
            actual = Colr[from].apply(null, input)[toArray]();
            compare(actual, round(expected));

            // fromRgb().toRgbObject()
            actual = Colr[from].apply(null, input)[toObject]();
            compare(actual, arr2obj(to, round(expected)));

            // fromRgb().toRawRgbArray()
            actual = Colr[from].apply(null, input)[toRawArray]();
            compare(round2(actual), expected);

            // fromRgb().toRawRgbObject()
            actual = Colr[from].apply(null, input)[toRawObject]();
            compare(round2(actual), arr2obj(to, expected));

            // fromRgbArray().toRgbArray()
            actual = Colr[fromArray](input)[toArray]();
            compare(actual, round(expected));

            // fromRgbArray().toRgbObject()
            actual = Colr[fromArray](input)[toObject]();
            compare(actual, round(arr2obj(to, expected)));

            // fromRgbArray().toRawRgbArray()
            actual = Colr[fromArray](input)[toRawArray]();
            compare(round2(actual), expected);

            // fromRgbArray().toRawRgbObject()
            actual = Colr[fromArray](input)[toRawObject]();
            compare(round2(actual), arr2obj(to, expected));

            // fromRgbObject().toRgbArray()
            actual = Colr[fromObject](arr2obj(from, input))[toArray]();
            compare(actual, round(expected));

            // fromRgbObject().toRgbObject()
            actual = Colr[fromObject](arr2obj(from, input))[toObject]();
            compare(actual, round(arr2obj(to, expected)));

            // fromRgbObject().toRawRgbArray()
            actual = Colr[fromObject](arr2obj(from, input))[toRawArray]();
            compare(round2(actual), expected);

            // fromRgbObject().toRawRgbObject()
            actual = Colr[fromObject](arr2obj(from, input))[toRawObject]();
            compare(round2(actual), arr2obj(to, expected));
          }
        });
      });
    }
  });

  describe('Alter', function () {
    for (var from in tests) {
      var test = tests[from];
      for (var method in test.alter) {
        itShouldAlter(method, test.alter[method]);
      }
    }

    function itShouldAlter (method, tests) {
      it('should alter using '+method, function () {
        tests.forEach(function (test) {
          var amount = test[0];
          var input = test[1];
          var expected = test[2];
          var actual = Colr.fromHex(input)[method](amount).toHex();
          compare(actual, expected);
        });
      });
    }
  });

