  /*
   * UTILS
   */

  function compare (actual, expected) {
    if (typeof expected === 'object') {
      assert.deepEqual(actual, expected);
    } else {
      assert.equal(actual, expected);
    }
  }

  function round (n) {
    if (Array.isArray(n)) {
      return n.map(Math.round);
    } else if (typeof n === 'object') {
      for (var key in n) {
        n[key] = Math.round(n[key]);
      }
    } else if (typeof n === 'number') {
      return Math.round(n);
    }
    return n;
  }

  function round2 (n) {
    if (Array.isArray(n)) {
      return n.map(function (n) { return Math.floor(n * 100) / 100; });
    } else if (typeof n === 'object') {
      for (var key in n) {
        n[key] = Math.floor(n[key] * 100) / 100;
      }
    } else if (typeof n === 'number') {
      return Math.floor(n * 100) / 100;
    }
    return n;
  }

  function arr2obj(type, arr) {
    type = type.toLowerCase();
    if (type.indexOf('rgb') > -1) {
      return { r: arr[0], g: arr[1], b: arr[2] };
    }
    if (type.indexOf('hsl') > -1) {
      return { h: arr[0], s: arr[1], l: arr[2] };
    }
    if (type.indexOf('hsv') > -1) {
      return { h: arr[0], s: arr[1], v: arr[2] };
    }
  }


